import React from 'react'

import { Feather } from '@expo/vector-icons'

import styled from 'styled-components/native'

const Container = styled.View`
	flex: 1;
	justify-content: flex-end;
	margin: 0 0 70px 13px;
`
const User = styled.View`
	flex-direction: row;
	align-items: center;
`
const UserName = styled.Text`
	font-size: 17px;
	color: rgba(255, 255, 255, 1);
	text-shadow: 3px 3px 3px rgba(0, 0, 0, 0.1);
	font-weight: bold;
	letter-spacing: -0.3px;
`
const Checked = styled.Image`
	width: 16px;
	height: 16px;
	margin: 0 5px;
`
const Description = styled.Text`
	font-size: 17px;
	color: rgba(255, 255, 255, 0.8);
	letter-spacing: -0.2px;
	margin-top: 6px;
	width: 80%;
`
const Music = styled.Text`
	font-size: 15px;
	color: #fff;
	margin-top: 13px;
	width: 80%;
`

const Info = ({ user }) => {
	return (
		<Container>
			<User>
				<UserName>@{user.data.userName}</UserName>
				<Checked source={require('../../services/assets/icons/checked.png')} />
			</User>
			<Description>{user.data.caption}</Description>
			<Music>
				<Feather name='music' size={13} />
				{"Masks off"}
			</Music>
		</Container>
	)
}

export default Info
