const theme = {
    colors: {
      primary: '#007AFF',
      white: '#FAFAFA',
      lightGray: '#EBEBEB',
      gray: '#CACACA',
      black: '#000000'
    },
    spacing: {
      xs: 4,
      sm: 8,
      m: 16,
      l: 32,
      xl: 40,
    },
    textVariants: {
      h1: {
        fontFamily: 'NunitoBold',
        fontSize: 24,
      },
      h2: {
        fontFamily: 'NunitoBold',
        fontSize: 18,
      },
      body1: {
        fontFamily: 'NunitoRegular',
        fontSize: 18,
      },
      body2: {
        fontFamily: 'NunitoRegular',
        fontSize: 16,
      },
      body3: {
        fontFamily: 'NunitoRegular',
        fontSize: 14,
      },
    },
    borderRadius: {
      xs: 1,
      m: 10,
    },
    imageHeight: {
      xs: 40,
      s: 100,
      m: 112,
      l: 123,
    },
  }
  
export default theme;
